<script>
  // @ts-nocheck

  import { onMount, afterUpdate, tick } from "svelte";
  import { page } from "$app/stores";
  import InfoPopup from "./InfoPopup.svelte";
  import Attribute from "./AttributeCard.svelte";
  import IconCard from "./IconCard.svelte";

  /**
   * @type {any}
   */
  export let gameSession;

  let host = "";
  let character = "Charakter";

  /**
   * @type {any}
   */
  let nameList;
  $: host, (host = $page.url.hostname);

  let showLoad = false;
  let showSave = false;
  let showNew = false;
  let showDelete = false;

  onMount(async () => {});

  const initpopup = (c) => {
    //console.log(c);
    new bootstrap.Popover(c, { html: true });
  };

  async function loadSessions() {
    const res = await fetch("http://" + host + ":8080/session/list/", {
      method: "GET",
    });

    return await res.json();
  }

  /**
   * @param {string} characterName
   */
  async function loadGameSession(characterName) {
    const res = await fetch("http://" + host + ":8080/session/load/", {
      method: "POST",
      body: JSON.stringify({
        name: characterName,
      }),
    });

    gameSession = await res.json();
  }

  async function saveGameSession() {
    const res = await fetch("http://" + host + ":8080/session/save/", {
      method: "POST",
      body: JSON.stringify({
        name: character,
        id: gameSession.id,
      }),
    });
  }

  async function translate(keyList) {
    if (Array.isArray(keyList)) {
      if (keyList[0].id) {
        var keys = keyList.map(function (item) {
          return item["id"];
        });

        console.log(keys);

        const res = await fetch("http://" + host + ":8080/translate/", {
          method: "POST",
          body: JSON.stringify({
            keys: keys,
          }),
        });

        return await res.json();
      } else {
        const res = await fetch("http://" + host + ":8080/translate/", {
          method: "POST",
          body: JSON.stringify({
            keys: keyList,
          }),
        });
        return await res.json();
      }
    } else {
      const res = await fetch("http://" + host + ":8080/translate/", {
        method: "POST",
        body: JSON.stringify({
          keys: [keyList],
        }),
      });
      return await res.json();
    }
  }

  /**
   * @param {int} position
   */
  async function dropItem(position) {
    const res = await fetch("http://" + host + ":8080/item/drop/", {
      method: "POST",
      body: JSON.stringify({
        position: position,
        id: gameSession.id,
      }),
    });

    gameSession = await res.json();
  }

  /*
  
   async function useItem(item) {
    const res = await fetch("http://" + host + ":8080/item/use/", {
      method: "POST",
      body: JSON.stringify({
        id: gameSession.id,
        idItem: item,
        modifiedAttribute: gameSession.character.currentAttribute,
        modificationValue:  
      }),
    });

    gameSession = await res.json();
  }
*/
</script>

<div class="bs-docs-section">
  {#if nameList}
    <div class="row" style="margin-top: 70px;">
      <div class="col-lg-12">
        <button type="button" class="btn btn-primary">Neuer Charakter</button>
        <div class="btn-group" role="group" aria-label="Button group with nested dropdown">
          <button type="button" class="btn btn-primary">Lade Charakter</button>
          <div class="btn-group" role="group">
            <button
              id="btnGroupDrop1"
              type="button"
              class="btn btn-primary dropdown-toggle"
              data-bs-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false" />
            <div class="dropdown-menu" aria-labelledby="btnGroupDrop1" style="">
              {#each nameList.names as name, i}
                <a class="dropdown-item" on:click={() => loadGameSession(name)}>{name}</a>
                <!--button type="button" class="btn btn-primary" on:click={() => doPost(i)}>{action.text}</button-->
              {/each}
            </div>
          </div>
        </div>
        <input bind:value={character} />
        <button type="button" class="btn btn-primary" on:click={() => saveGameSession()}>Speicher Charakter </button>
      </div>
    </div>
  {/if}

  <div class="row" style="margin-top: 90px;">
    <div class="col-lg-12" style="">
      <div class="bs-component flexwrap">
        <div class="card mb-3 flexwrap">
          <div class="card-header flexwrap">
            <img
              height="48px"
              width="48px"
              src="data:image/svg+xml;base64,"
              alt="icon" />
            <span style="margin-left: 15px; font-weight: bold; display: flex; align-items: center;">Einsamer Wolf</span>
            <div class="flexwrap" style="align-items: center; justify-content: end;">
              <button
                type="button"
                style="height: 48px;"
                on:click={() => {
                  showLoad = !showLoad;
                }}>
                <img
                  width="36"
                  height="36"
                  src="data:image/svg+xml;base64,"
                  alt="Load" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  {#if showLoad}
    <div class:active={showLoad} class:show={showLoad} class="row fade">
      <div class="col-lg-4">
        <div class="bs-component">
          <div class="card mb-3">
            <div class="card-body">
              <img src="" alt="icon" />
              <span style="margin-left: 15px; font-weight: bold;">Laden</span>
            </div>
          </div>
          <div>
            {#await loadSessions()}
              LOAD SESSIONS<br />
            {:then sessions}
              {#each sessions.names as name, i}
                <li class="list-group-item d-flex justify-content-between align-items-left">
                  <span>
                    {name}
                  </span>
                </li>
              {/each}
            {/await}
          </div>
        </div>
      </div>
      <div class="col-lg-4">
        <div class="bs-component">
          <div class="card mb-3">
            <div class="card-body">
              <img
                src="data:image/png;base64,"
                alt="icon" />
              <span style="margin-left: 15px; font-weight: bold;">Speichern</span>
            </div>
          </div>
        </div>
      </div>
      <div class="col-lg-4">
        <div class="bs-component">
          <div class="card mb-3">
            <div class="card-body">
              <img
                src="data:image/png;base64,"
                alt="icon" />
              <span style="margin-left: 15px; font-weight: bold;">Löschen</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  {/if}

  {#if !showLoad && gameSession && gameSession.character}
    <div class="row">
      <Attribute
        image="data:image/svg+xml;base64,"
        name="Ausdauer"
        currentValue={gameSession.character.endurance.value}
        maxValue={gameSession.character.endurance.maxValue} />
      <Attribute
        image="data:image/svg+xml;base64,"
        name="Kampfstärke"
        currentValue={gameSession.character.battleStrength.value}
        maxValue={gameSession.character.battleStrength.maxValue} />
      <Attribute
        image="data:image/svg+xml;base64,"
        name="Gold"
        currentValue={gameSession.character.gold.value}
        maxValue={gameSession.character.gold.maxValue} />
      <Attribute
        image="data:image/svg+xml;base64,"
        name="Nahrung"
        currentValue={gameSession.character.food.value}
        maxValue={gameSession.character.food.maxValue} />
    </div>

    <div class="row">
      <IconCard
        cols={4}
        caption="Kai Fähigkeiten"
        src="data:image/svg+xml;base64,">
        <ul class="list-group list-group-flush">
          {#if gameSession.character.skills}
            {#await translate(gameSession.character.skills)}
              {#each gameSession.character.skills as skill}
                {skill}<br />
              {/each}
            {:then translation}
              {#each translation.translations as t, i}
                <li class="list-group-item d-flex justify-content-between align-items-left">
                  <span>
                    <img style="margin-right: 10px" src={t.png} alt="icon" />
                    {t.de}
                  </span>
                  <InfoPopup tooltip={t.tooltip} title={t.de} />
                </li>
              {/each}
              <li class="list-group-item d-flex justify-content-between align-items-left" />
            {/await}
          {:else}
            Keine<br />
          {/if}
        </ul>
      </IconCard>

      <IconCard
        cols={4}
        caption="Waffen"
        src="data:image/svg+xml;base64,">
        <ul class="list-group list-group-flush">
          {#if gameSession.character.weaponOne}
            
              <li class="list-group-item d-flex justify-content-between align-items-left">
                <span>
                  <img style="margin-right: 10px" src={gameSession.character.weaponOne.image} alt="icon" />
                  {gameSession.character.weaponOne.text}
                </span>
                <InfoPopup tooltip={gameSession.character.weaponOne.tooltip} title={gameSession.character.weaponOne.text} />
              </li>
            
          {/if}
          {#if gameSession.character.weaponTwo}
            
              <li class="list-group-item d-flex justify-content-between align-items-left">
                <span>
                  <img style="margin-right: 10px" src={gameSession.character.weaponTwo.image} alt="icon" />
                  {gameSession.character.weaponTwo.text}
                </span>
                <InfoPopup tooltip={gameSession.character.weaponTwo.tooltip} title={gameSession.character.weaponTwo.text} />
              </li>
            
            <li class="list-group-item d-flex justify-content-between align-items-left" />
          {/if}
        </ul>
      </IconCard>

      <IconCard
        cols={4}
        caption="Ausrüstung"
        src="data:image/svg+xml;base64,">
        <ul class="list-group list-group-flush">
          {#if gameSession.character.items}
            {#each gameSession.character.items as item, i}
              <li class="list-group-item d-flex justify-content-between align-items-center">
                <span>
                  <img width="32px" height="32px" style="margin-right: 10px" src={item.image} alt="icon" />
                  {item.text}
                </span>
                <div>
                  <button on:click={() => dropItem(i)} type="button" class="btn btn-dark btn-sm">
                    <img
                      width="16px"
                      height="16px"
                      src="data:image/png;base64,"
                      alt="icon" />
                  </button>

                  <InfoPopup tooltip={item.tooltip} title={item.text} />
                </div>
              </li>
            {/each}
          {/if}
        </ul>
      </IconCard>
    </div>
  {/if}
</div>

<style>
  .flexwrap {
    display: flex;
    flex-grow: 1;
    flex-wrap: wrap;
  }
</style>
